//TYPES

const GET_PRODUCTS = "GET_PRODUCTS"
const SET_CART = "SET_CART"
const DELETE_CART = "DELETE_CART"

export default function Reducer(state, action){
    //payload es el dato
    //type es el tipo de operacion
    const {payload, type} = action
    switch(type){
        case GET_PRODUCTS:
            return{...state, products: payload}
        case SET_CART:
            console.log("set cart-state.product", state.products);
            console.log("set cart-state.PAYLOAD", payload);
            console.log("set cart- state", state);
            return{
               ...state,
               cart: [
                ...state.carrito,
                state.products.filter((ite) => ite.id == payload)]
            }
    }
}
